src/app/app.components.scss.content:
@import "~swiper/swiper";
@import "~swiper/components/scrollbar/scrollbar";
html,
body {
  position: relative;
  height: 100%;
}

body {
  background: #fff;
}

.swiper-container {
  width: 100%;
  height: 100%;
}

.swiper-slide {
  font-size: 18px;
  height: auto;
  -webkit-box-sizing: border-box;
  box-sizing: border-box;
  padding: 30px;
}

src/styles.scss.content:
html,
body {
  position: relative;
  height: 100%;
}

body {
  background: #eee;
  font-family: Helvetica Neue, Helvetica, Arial, sans-serif;
  font-size: 14px;
  color: #000;
  margin: 0;
  padding: 0;
}
